INSERT INTO `script_texts` VALUES
(-1999926, 'Bow down! Kneel before the Ashbringer! A new dawn approaches, brothers and sisters! Our message will be delivered to the filth of this world through the chosen one!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 6, 0, 0, 'mograine_ashbringer_enter'),
(-1999927, 'Have you come to save the world? To Cleanse it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_1'),
(-1999928, 'Kneel! Kneel before the Ashbringer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_2'),
(-1999929, 'And so it begins...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_3'),
(-1999930, 'My Lord, please allow me to live long enough to see you purge this world of the infidels.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_4'),
(-1999931, 'Take me with you sir', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_5'),
(-1999932, 'Ashbringer', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_6'),
(-1999933, 'I am unworhty, sir.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_ashbringer_7'),
(-1999934, 'You hold my father\'s blade, $N. My soldiers are yours to control, my $GLord:Lady;. Take them... Lead them... The impure must be purged. They must be cleansed of their taint.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'say_mograine_ash_1'),
(-1999935, 'Renault...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 25, 'say_mograine_ash_2'),
(-1999936, 'Father... But... How?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 6, 'say_mograine_ash_3'),
(-1999937, 'Did you think that your betrayal would be forgotten? Lost in the carefully planned cover up of my death? Blood of my blood, the blade felt your cruelty long after my heart had stopped beating. And in death, I knew what you had done. But now, the chains of Kel\'Thuzad hold me no more. I come to serve justice. I AM ASHBRINGER.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 'say_mograine_ash_4'),
(-1999938, 'Forgive me, father! Please...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 20, 'say_mograine_ash_5'),
(-1999939, 'You are forgiven...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'say_mograine_ash_6'),
(-1999940, 'The light condemns all who harbor evil.  Now you will die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'scarlet_monastery_aggro_1'),
(-1999941, 'You carry the taint of the scourge.  Prepare to enter the twisting nether.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'scarlet_monastery_aggro_2'),
(-1999942, 'There is no escape for you.  The Crusade shall destroy all who carry the scourge\'s taint.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'scarlet_monastery_aggro_3'),
(-1999943, 'The Scarlet Crusade shall smite the wicked and drive evil from these lands!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 'scarlet_monastery_aggro_4'),
(-1000007, '%s attempts to run away in fear!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 'EMOTE_GENERIC_FLEE');
